var  power = util.rms 

function map_f_on_dict(d,f) {
    for (var k in d ) {
	d[k] = f(d[k]) 
    }
    return d 
}

function reduce_val(v) {

    if (typeof v == "object") { 
	if (Array.isArray(v)) {
	    // its an array
	    return power(v) 

	} else {
	    // assume its a dict
	    return map_f_on_dict(v, reduce_val) 
	}
    } else {

	// assume its a number
	return v  

    }

}

var reduce = reduce_val 

function reduce_dict(obj) {
    return map_f_on_dict(obj, reduce_val)
}

